import Modal from 'react-bootstrap/Modal';
import {Button} from 'react-bootstrap';
function FavList(){
const [favArr,setFavArr] = useState([]);
function ModalMovie({cardInfo, show, handleClose}) {
    const getFavmovie = () =>{
    const serverURL = `http://localhost:3003/addmovie`;
    fetch(serverURL)
    .then((response)=>{
        response.json()
        .then(data=>{
            setFavArr(data)
            console.log(data)
        })
    })
}

useEffect(()=>{
    getFavmovie()
},[])

    return (
        <>
            <Modal show={show} onHide={handleClose} animation={false}>
                <Modal.Header closeButton>
                    <Modal.Title>Add It To Favorite</Modal.Title>
                </Modal.Header>
                <Modal.Body className="body">
                    <h3>{cardInfo.title}</h3>
                    <img alt="" src={cardInfo.image} />
                    <div>
                        <label htmlFor="op">Write Your Opinion</label>
                        <input placeholder="Write Your Opinion" type="text" id="op" />
                    </div>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="primary"> Add To Favorite </Button>
                </Modal.Footer>
            </Modal>
        </>
    )
}
}
export default ModalMovie;
