import { useState, useEffect } from "react";
import axios from "axios";
import { Button, Container, Row, Card, Col } from "react-bootstrap";
import ModalMovie from "../ModalMovie/ModalMovie";

function Home() {
  const [data, setData] = useState([]);
  const [cardInfo, setCardInfo] = useState({});
  const [show,setShow] = useState(false);

  const handleClose = ()=>setShow(false);

  const getAllMovies = () => {
    // return await axios
    //   .get(`http://localhost:3003/trending`)
    //   .then((result) => {
    //     return result.data;
    //   })
    //   .catch((err) => {
    //     console.log(err);
    //   });

      fetch(`http://localhost:3003/trending`)
      .then(response => {
          response.json().then(data => {
              console.log(data)
              setData(data)

          })
      })
  };


  useEffect(() => {
    void (async () => {
      let data = await getAllMovies();
      setData(data);
    })();
  }, []);



  return (
    <>

      <Container className="div-container">
        <Row md={3}>
          {data.length &&
            data.map((ele) => (
              <Col key={ele.id} md={4}>
                <Card className="div-card">
                  <Card.Img
                    className="div-card-img"
                    variant="top"
                    src={`https://image.tmdb.org/t/p/w500/${ele.poster_path}`}
                  />
                  <Card.Body>
                    <Card.Title className="div-card-title">
                      {ele.title}
                    </Card.Title>
                    <Card.Link className="div-card-link">
                      {ele.overview}
                    </Card.Link>
                    <div>
                      <Button
                        className="div-card-button"
                        variant="primary"
                        onClick={() => {
                          setCardInfo(ele);
                          setShow(true);
                        }}
                      >
                        Add To Favorite
                      </Button>
                    </div>
                  </Card.Body>
                </Card>
              </Col>
            ))}
        </Row>
      </Container>
      {<ModalMovie cardInfo={cardInfo} show={show} handleClose={handleClose} />}
      {!data.length && (
        <div>
          <h2>No Such Results, Please try again later</h2>
        </div>
      )}
    </>
  );
}

export default Home;
